import React, { useState } from 'react'

const Form = props =>{
    const { onSubmitHandler } = props;
    const {first_name, setFirst_Name} = useState("");
    const {last_name, setLast_Name} = useState("");
    const {email, setEmail} = useState("");
    const {avatar, setAvatar} = useState("");
    return(
        <form onSubmit = {e=>{onSubmitHandler(e, {first_name, last_name, email, avatar})}}>
            <p>
                <label>First Name</label>
                <input type="text" name="first_name" onChange={(e) =>{setFirst_Name(e.target.value)}}/>
            </p>
            <p>
                <label>Last Name</label>
                <input type="text" name="last_name" onChange={(e) =>{setLast_Name(e.target.value)}}/>
            </p>
            <p>
                <label>E-Mail Address</label>
                <input type="text" name="email" onChange={(e) =>{setEmail(e.target.value)}}/>
            </p> 
            <p>
                <input type="image" name="avatar" alt ="avatar" width="100" height="100" onChange={(e) =>{setAvatar(e.target.value)}} />
            </p>
            <input type="submit"/>
        </form>
    )
}
export default Form;
